    public void Consultar(int pag)
    {
        switch (Filtro)
        {
            case "0": //Todo
                ListaCategorias = CategoriasBLL.GetList(c => true);
                break;
            case "1": //ClienteId
                if (Criterio.Trim().Length > 0)
